export const config = {
 name: 'SendMessage',
 type: 'api',
 path: '/messages',
 method: 'POST',
 emits: ['message.sent']
};

export const handler = async(req, {emit}) => {
 await emit({topic:'message.sent', data: { text: req.body.text }});
 return{ status:200, body: { ok:true} };
};

export const config = {
 name: 'ProcessMessage',
 type: 'event',
 subscribes: ['message.sent']
};

export const handler = async(input, {logger}) => {
 logger.info('Processing message', input);
};
